<template>
	<view class="page-login">
		<view class="title-line">
			<view class="logo"></view>
			<view class="title-text">智驾数据服务平台</view>
		</view>
		<view class="form-wrapper">
			<u--form :model="formData" ref="uForm" :rules="rules">
				<u-form-item prop="acount" borderBottom ref="item1"> 
					<u-input v-model="formData.acount" border="none" placeholder="请输入域账号" prefixIcon="account"
						prefixIconStyle="font-size: 22px;color: #909399"
					></u-input>
				</u-form-item>
				<view class="password-form-item">
					<u-form-item prop="password" borderBottom ref="item1">
						<u-input v-model="formData.password" border="none" :type="passwordShow?'text':'password'" placeholder="请输入密码" prefixIcon="lock"
							prefixIconStyle="font-size: 22px;color: #909399"
						></u-input>
					</u-form-item>
					<view class="eyeicon" @click="changeInputType">
						<u-icon :name="passwordShow?'eye-fill':'eye-off'" color="#fff" size="20"></u-icon>
					</view>
				</view>
				<!-- <view class="verifycode-form-item">
					<u-form-item prop="verifyCode" borderBottom ref="item1">
						<u-input v-model="formData.verifyCode" border="none" placeholder="请输入验证码" prefixIcon="lock-open"
							prefixIconStyle="font-size: 22px;color: #909399"
						></u-input>
					</u-form-item>
					<view class="verify-code-box" @click="updateImageCode">
					  <canvas style="width:155rpx;height:74rpx;" canvas-id="gVerify"></canvas>
					</view>
				</view> -->
				<view class="btn-box">
					<u-button color="#423bb3" text="确定" shape="circle" :loading="isBtnLoading" @click="onLogin"></u-button>
				</view>
			</u--form>
			<!-- #423bb3 #efa500-->
		</view>
		<!-- <u-button color="#423bb3" text="getcode" shape="circle" @click="getVerifyCode"></u-button> -->
		<view class="footer-box">
			<view class="footer-text">智驾中心</view>
		</view>
	</view>
</template>

<script>
	import JSEncrypt from '@/common/jsencrypt/bin/jsencrypt.js' // RSA 加密
	import Base64 from 'base-64'
	import { mapState } from 'vuex'
	import apiLib from '@/api/api.js'
	import {isNotNil} from '@/lib/lib.js'
	import { GVerify  } from '@/lib/GVerify'
	
	export default {
		data() {
			return {
				public_key:'',
				formData:{acount:'',password:''},
				isBtnLoading:false,
				passwordShow:false,
				verifyCode:'', // 生成的随机验证码
				rules:{
					'acount': {
						type: 'string',
						required: true,
						message: '请输入域账号',
						trigger: ['blur', 'change']
					},
					'password': {
						type: 'string',
						required: true,
						message: '请输入密码',
						trigger: ['blur', 'change']
					}
				},
				share:{
					title:'智驾数据服务平台',
					path:'/pages/index/index',
					imageUrl:'',
					desc:'',
					content:''
				}
			}
		},
		computed: {
			...mapState(['openid'])
		},
		onLoad() {
		},
		onShow: function() {
			
		},
		onReady(){
			this.getPublicKey()
			this.gVerify = new GVerify({
				el: 'gVerify',
				width: 80,
				height: 35,
				createCodeImg: ""
			});
		},
		methods: {
			async onLogin(){ // 用户点击确定进行登录
				this.$refs.uForm.validate().then(async valid=>{
					if(valid){
						// const verifyCodeValidate = this.gVerify.validate(this.formData.verifyCode)
						// if(!verifyCodeValidate){
						// 	uni.$u.toast('验证码错误!')
						// 	return
						// }
						
						const wxRes = await apiLib.wxLogin()
						console.log(wxRes,'wxRes')
						if(wxRes.isOk){
							this.formData.js_code = wxRes.js_code
							this.submit()
						}
						console.log(this.formData,'this.formData')
					}
				}).catch(err=>{
					// uni.$u.toast('表单校验失败')
					console.log(err,'2323')
				})
			},
			async submit(){
				const encryptor = new JSEncrypt();
				const publickKeyBase64 = Base64.decode(this.public_key)
				encryptor.setPublicKey(publickKeyBase64)
				
				const params = {
					acount:encryptor.encrypt(this.formData.acount), // rsa加密后的值
					password:encryptor.encrypt(this.formData.password),
					js_code:this.formData.js_code
				}
				
				this.isBtnLoading = true
				
				const res = await apiLib.login(params)
				
				if(res.isOk){ // 登录成功，跳转至功能页面
					const {data} = res || {data:{}}
					this.$store.dispatch('baseStore/updateToken',data.token)
					this.$store.dispatch('baseStore/updateOpenid',data.openid)
					this.$store.dispatch('baseStore/updateUserInfo',data.userInfo)
					this.$store.dispatch('baseStore/updateAuthResourceLib',data.resources)
					this.toHome()
				}else{
					const {data} = res || {data:{}}
					uni.$u.toast(data.message || '登录失败，请稍后再试！')
				}
				
				this.isBtnLoading = false
			},
			async getPublicKey(){ // 获取公钥加密
				const res = await apiLib.getPublicKey()
				if(res.isOk){
					this.public_key = res.public_key
				}
			},
			toHome(){
				uni.switchTab({
					url:'/pages/tabs/tab-workbench/tab-workbench'
				})
			},
			changeInputType(){
				console.log('changeInputType')
				this.passwordShow = !this.passwordShow
			},
			updateImageCode() { // 刷新校验码
			  this.gVerify.refresh()
			}
		}
	}
</script>

